.responsiveTable table,
.responsiveTable thead,
.responsiveTable tbody,
.responsiveTable th,
.responsiveTable td,
.responsiveTable tr {
display: block;
}

/* Hide table headers (but not display: none;, for accessibility) */
.responsiveTable thead tr {
position: absolute;
top: -9999px;
left: -9999px;
}

.responsiveTable tr {
border: 1px solid #acd8f5;
margin-bottom: 10px;
}

.responsiveTable td {
/* Behave like a "row" */
border: none;
position: relative;
padding: 3px 3px 3px 50%;
white-space: normal;
text-align:left;
background: white;
}

.responsiveTable td:before {
/* Now like a table header */
position: absolute;
/* Top/left values mimic padding */
top: 3px;
left: 6px;
width: 45%;
padding-right: 10px;
white-space: nowrap;
text-align:left;
font-weight: bold;
}

